#EJERCICIO 2clear all;clc;#Definimos las funciones con las que vamos a trabajarfunction y = f(x) y = x.^3 + 9*x + 9; endfunctiong1 = @(x) - x.^3 / 9 - 1;g2 = @(x) - 9 ./ (x.^2 + 9); #./ es para dividir entre un vectorg3 = @(x) x.^3 + 10*x + 9;g4 = @(x) (2*x.^3 - 9) ./ (3 * (x.^2 + 3));#Intervalo de trabajoa = -3;b = 0;#absisas para las gráficasx = linspace (a, b, 30); # con 30 ya daría impresión de suavidad#GRAFICASclf; #borra las figuras anterioresfigure(1);hold on; #preparate que voy a superponer cosas. No me borres lo anteriorplot (x, x, 'k'); #k = blackaxis ( [a b a b] ); #tamaño de la gráficaplot (x, g1(x), 'r; g1;',      x, g2(x), 'b; g2;',      x, g3(x), 'g; g3;',      x, g4(x), 'm; g4;');grid on; #cuadricula de la gráficahold off; #ya he dejado de superponer cosasfigure (2);plot ([a b], [0 0], 'k', x, f(x), 'b; f(x);');#tambien se puede lo siguiente:#plot ([a b], [0 0], 'k'); plot (x, f(x), 'b; f(x);');grid on;#TABLA DE ITERACIONES (10)x1 = -1.5;x2 = x1;x3 = x1;x4 = x1;for i = 1 : 10  x1 = [x1 g1(x1(i))];  x2 = [x2 g2(x2(i))];  x3 = [x3 g3(x3(i))];  x4 = [x4 g4(x4(i))];endfor  disp ("Resultados de las itercaciones");disp ( [0:10; x1; x2; x3; x4]' ); # la ' traspone la matrizdisp ("Diferencias de las iteraciones");disp ( [1:10;        abs(x1(2:11) - x1(1:10));        abs(x2(2:11) - x2(1:10));        abs(x3(2:11) - x3(1:10));        abs(x4(2:11) - x4(1:10))        ]');disp ("Conclusión: la mas rapida es g4 seguida de g2 y despues g1. g3 diverge.");