clear all;clc;function y = f(x)  y = x.^3 + 9*x.^2 - 6 * exp(cos(pi .* x));endfunctionfunction y = g(x)  y = x - 0.016 * f(x);endfunction#EJERCICIO 1x = linspace(0,1,50);plot([0 1], [0 1], 'b', x, g(x), 'r');#EJERCICIO 2x0 = 0.5;printf('%6.8f \n', x0);for n = 1:15  x0 = g(x0);  printf('%6.8f \n', x0);endforif abs(x0 - g(x0)) < 0.01;  disp('Podemos afirmar que hay convergencia');else   disp('O la convergencia es muy lenta, o hay divergencia');endif